function changeLanguage(lang) {
    document.querySelectorAll('[data-lang]').forEach(el => {
        const key = el.getAttribute('data-lang');
        el.textContent = translations[lang][key] || `No translation for ${key}`;
    });
    // Update texts not controlled by data-lang attributes
    document.getElementById('welcome').textContent = translations[lang]['welcome'];
    document.getElementById('slogan').textContent = translations[lang]['slogan'];
    document.getElementById('start-learning').textContent = translations[lang]['start-learning'];
}

